import React, { Component } from 'react';

class Form extends Component {
  render() {
    return (
      <div>
        <h1>States and React</h1>
        <form className="form">
          <label>
            Type your favorite state
            <textarea name="favoriteState" value={this.state.favoriteState} onChange={this.handleChange} />
          </label>
          <input
            type="number"
            name="age"
          />
          <input
            type="checkbox"
            name="willAttend"
          />
        </form>
      </div>
    );
  }
}

export default Form;
