import React, { Component } from 'react';

class Form extends Component {
  constructor() {
    super()

    this.state = {
      favoriteState: '',
      age: 0,
      willAttend: false,
    }
  }

  handleTextAreaChange() {
    console.log(Event)
  }

  render() {
    return (
      <div>
        <h1>States and React</h1>
        <form className="form">
          <label>
            Type your favorite state
            <textarea name="favoriteState" onChange={this.handleTextAreaChange}/>
          </label><br/>
          <label>
            How old are you?
            <input
              type="number"
              name="age"
            />
          </label><br/>
          <label>
            Will you attend?
            <input
              type="checkbox"
              name="willAttend"
            />
          </label>  
        </form>
      </div>
    );
  }
}

export default Form;
