import React, { Component } from 'react';

class Form extends Component {
  constructor() {
    super()

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      favoriteState: '',
      age: 0,
      willAttend: false,
    }
  }

  handleChange({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value
    });
  }

  render() {
    return (
      <div>
        <h1>States and React</h1>
        <form className="form">
          <label>
            Type your favorite state
            <textarea 
              name="favoriteState" 
              value={this.state.favoriteState} 
              onChange={this.handleChange}
            />
          </label><br/>
          <label>
            How old are you?
            <input
              type="number"
              name="age"
              value={this.state.age}
              onChange={this.handleChange}
            />
          </label><br/>
          <label>
            Will you attend?
            <input
              type="checkbox"
              name="willAttend"
              value={this.state.willAttend}
              onChange={this.handleChange}
            />
          </label>  
        </form>
      </div>
    );
  }
}

export default Form;
